function xformItem(item) {
    return {
        id: item.key,
        name: item.displayName,
        type: item.itemClass,
        subType: item.meta.itemType,
        desc: item.descriptionHTML,
        dlc: item.meta.dlc,
        colors: item.meta.itemColor,
        tags: item.meta.itemTags,
        thumb: item.thumbnail,
        wiki: item.wikiPage
    };
}

